// I only use the number of unique pieces, not the total number of pieces.  This
// is because we want a key match on "equivilent" boards.  i.e. if we transposed
// two same-colored rooks, the board is functionally the same.
const int BOARD_SIZE = 10 * 10;   // size of chess board
const int NUM_CHANNEL = 5; // empty blackQueue whiteQueue stone stage

const int EMPTY = 0;
const int WHITEQUEUE = -1;
const int BLACKQUEUE = 1;
const int STONE = 2;
const int SELECT = 3;


class ZobristHash
{

    //Zobrist 哈希表是一个三维数组，用于存储每个位置、每个棋子类型和每个颜色的随机数（Zobrist 键）
	//uint64_t m_aZobristTable[BOARD_SIZE][NUM_PIECES][NUM_COLORS];
	uint64_t m_aZobristTable[BOARD_SIZE][NUM_CHANNEL];

    //sqlite3* db = NULL;
    //sqlite3_stmt* stmt;
    std::mutex dbMutex;

    rwlock::Lock lock_;


public:
	void InitializeZobristTable(); // init hashTable

    ZobristHash();
    ~ZobristHash();
	int GetChessType(int);
    uint64_t CalculateZobristKey(int, const int[][10], int select=-1); // 用于计算当前棋盘状态的 Zobrist 键
    uint64_t UpdateZobristKey(uint64_t, int, int, int, int); // 更新hash

    // insert
    void write_thread(Sqlite*, InfoStruct&);
    void write(uint64_t, std::pair<torch::Tensor, float>);
    void writeTest();

    // read
    std::pair<torch::Tensor, float> read_thread(Sqlite* db, uint64_t query);
    void readTest();
};

